CREATE TEMPORARY TABLE TEMP_ETIKETTEN_OBJECT_LINK AS
    SELECT obj, etikette
    FROM ETIKETTEN_OBJECT_LINK 
    GROUP BY obj, etikette HAVING count(*) < 2;
INSERT INTO TEMP_ETIKETTEN_OBJECT_LINK 
	SELECT obj, etikette from ETIKETTEN_OBJECT_LINK where obj IN (
	SELECT obj 
	FROM ETIKETTEN_OBJECT_LINK
	GROUP BY obj, etikette
	HAVING count(*) > 1
	) GROUP BY obj, etikette HAVING count(*) > 1;
DELETE FROM ETIKETTEN_OBJECT_LINK;
INSERT INTO ETIKETTEN_OBJECT_LINK (obj, etikette) select obj, etikette from TEMP_ETIKETTEN_OBJECT_LINK;
DROP TABLE TEMP_ETIKETTEN_OBJECT_LINK;

CREATE TEMPORARY TABLE TEMP_ETIKETTEN_OBJCLASS_LINK AS
    SELECT objclass, sticker
    FROM ETIKETTEN_OBJCLASS_LINK 
    GROUP BY objclass, sticker HAVING count(*) < 2;
INSERT INTO TEMP_ETIKETTEN_OBJCLASS_LINK 
	SELECT objclass, sticker from ETIKETTEN_OBJCLASS_LINK where objclass IN (
	SELECT objclass 
	FROM ETIKETTEN_OBJCLASS_LINK
	GROUP BY objclass, sticker
	HAVING count(*) > 1
	) GROUP BY objclass, sticker HAVING count(*) > 1;
DELETE FROM ETIKETTEN_OBJCLASS_LINK;
INSERT INTO ETIKETTEN_OBJCLASS_LINK (objclass, sticker) select objclass, sticker from TEMP_ETIKETTEN_OBJCLASS_LINK;
DROP TABLE TEMP_ETIKETTEN_OBJCLASS_LINK;

UPDATE kontakt SET istPerson = '1', istPatient = '1'
WHERE kontakt.id IN (
  SELECT DISTINCT PatID
  FROM ch_elexis_omnivore_data
  WHERE ch_elexis_omnivore_data.PatID = kontakt.id and kontakt.istPatient = '0'
);
